						$output .= '<li><img src="'.$technologyLogo.'" height="15" width="15"> <a href="'.$node->get('field_url')->value.'">'.$node->getTitle().'</a></li>';
						if( $count == (int)($nodesCount/2) ) {
							$output .= '</ul></div><div class="col-6"><ul class="project-list">';
						}
